INSERT INTO `pmb_postmeta` VALUES(504, 164, 'pmb_article_link', 'http://localhost:8888/pmb/wp-content/uploads/2013/09/egypt_march_1999.pdf');
INSERT INTO `pmb_postmeta` VALUES(505, 164, 'pmb_article_publisher', 'National Geographic Traveler');
INSERT INTO `pmb_postmeta` VALUES(506, 164, 'pmb_article_publish_date', 'March 1999');
INSERT INTO `pmb_postmeta` VALUES(507, 164, 'pmb_article_excerpt', 'In the Royal Mummy Room of Cairo''s renowned Egyptian Museum, I gaze through a glass case at the wizened, leathery features of Ramses the Great (Ramses II)—the man who wore the double crown of Upper and Lower Egypt for an incredible 67 years (1279-1213 B.C.), one of the world''s longest reigns.');
INSERT INTO `pmb_postmeta` VALUES(508, 165, '_edit_last', '1');
INSERT INTO `pmb_postmeta` VALUES(509, 165, '_edit_lock', '1388707652:1');
INSERT INTO `pmb_postmeta` VALUES(510, 165, '_thumbnail_id', '163');
INSERT INTO `pmb_postmeta` VALUES(511, 165, 'pmb_article_link', 'http://localhost:8888/pmb/wp-content/uploads/2013/09/vietnam_may_june_1996.pdf');
INSERT INTO `pmb_postmeta` VALUES(512, 165, 'pmb_article_publisher', 'National Geographic Traveler');
INSERT INTO `pmb_postmeta` VALUES(513, 165, 'pmb_article_publish_date', 'May/June 1996');
INSERT INTO `pmb_postmeta` VALUES(514, 165, 'pmb_article_excerpt', 'The well-traveled Peugeot labored up the winding stretch of National Highway 1 north of Danang, along the central coast of Vietnam. We were climbing toward Hai Van Pass, the Pass of the Ocean Clouds, in the mountains that cut across Vietnam''s skinny midsection, separating the steamy southern half of this thousand-mile-long country from the cooler north.');
