INSERT INTO `pmb_postmeta` VALUES(521, 166, 'pmb_article_publish_date', 'September/October 1995');
INSERT INTO `pmb_postmeta` VALUES(522, 166, 'pmb_article_excerpt', '“Did you know that bananas sing?” Monica Zijdemans laughed as she drove her vintage Volvo through the rolling Trelawny hills, the wind whipping her dark hair. Deftly dodging potholes in the narrow road, the freespirited Jamaican launched into her rendition of the sound that the leaves of banana trees make when the breeze blows through them just so.');
