INSERT INTO `pmb_posts` VALUES(398, 1, '2014-04-30 19:11:11', '2014-04-30 23:11:11', '', '<span class="ital">Louisville Courier-Journal</span>', '“Great reading abounds in this addictive collection of tales of the disreputable. From serial killers to snake oil salesmen, con artists to counterfeiters, author Martin tells very good tales of people being very, very bad.”', 'publish', 'closed', 'closed', '', 'test', '', '', '2014-05-09 20:06:28', '2014-05-10 00:06:28', '', 0, 'http://localhost:8888/pmb/?post_type=review&#038;p=398', 3, 'review', '', 0);
