INSERT INTO `pmb_posts` VALUES(402, 1, '2014-04-30 19:23:37', '2014-04-30 23:23:37', '', '<span class="ital">San Francisco Book Review</span>', '“This fascinating little book is filled with well-researched and very well-written accounts of twenty-eight exceptionally nasty people.... Not all in this book are soaked in blood. Some are thieves of epic proportions, others con-men, spies, hedonists, and some simply fools.... Author Paul Martin doesn’t know the meaning of the word boring.” Five Stars.', 'publish', 'closed', 'closed', '', 'san-francisco-book-review', '', '', '2014-05-09 20:05:48', '2014-05-10 00:05:48', '', 0, 'http://localhost:8888/pmb/?post_type=review&#038;p=402', 4, 'review', '', 0);
INSERT INTO `pmb_posts` VALUES(403, 1, '2014-04-30 19:24:12', '2014-04-30 23:24:12', '', '<span class="ital">Booklist</span>', '“This collection of exceptionally well-crafted historical portraits deals, in descending order, with monsters, the merely evil, and the unscrupulous.... Apart from his literate writing, Martin also has the knack for passing harsh judgments when they are deserved and also withholding them when they are not. A model of how to write short popular history.” ', 'publish', 'closed', 'closed', '', 'booklist-2', '', '', '2014-05-09 20:06:41', '2014-05-10 00:06:41', '', 0, 'http://localhost:8888/pmb/?post_type=review&#038;p=403', 5, 'review', '', 0);
INSERT INTO `pmb_posts` VALUES(404, 1, '2014-04-30 19:24:27', '2014-04-30 23:24:27', '', '<span class="ital">Baker & Taylor Forecast</span>', '“This wildly entertaining celebration of villains, scoundrels and rogues throughout history presents 30 colorful biographies of America''s most outrageous characters, including Ed Gein, who was Alfred Hitchcock’s inspiration for <span class="ital">Psycho</span>, [along with] the worst female serial killer ever.”', 'publish', 'closed', 'closed', '', 'baker-taylor-forecast', '', '', '2014-05-09 20:08:25', '2014-05-10 00:08:25', '', 0, 'http://localhost:8888/pmb/?post_type=review&#038;p=404', 6, 'review', '', 0);
INSERT INTO `pmb_posts` VALUES(406, 1, '2014-04-30 19:24:46', '2014-04-30 23:24:46', '', '<span class="ital">Publishers Weekly</span>', '“Martin delivers the reverse of his <span class="ital">Secret Heroes</span>, highlighting Americans who have negatively impacted society in a variety of stomach-churning ways.... They make a pretty reprehensible bunch, but Martin puts their crimes in context while keeping the book entertaining and informative.” Starred Review.', 'publish', 'closed', 'closed', '', 'publishers-weekly-2', '', '', '2014-05-09 20:09:15', '2014-05-10 00:09:15', '', 0, 'http://localhost:8888/pmb/?post_type=review&#038;p=406', 7, 'review', '', 0);
