INSERT INTO `pmb_posts` VALUES(484, 1, '2018-01-20 08:11:52', '2018-01-20 13:11:52', 'L0003723 Henry Solomon Wellcome: three-quarter length. Oil painting\nCredit: Wellcome Library, London. Wellcome Images\nimages@wellcome.ac.uk\nhttp://wellcomeimages.org\nHenry Solomon Wellcome, 1906: three-quarter length. Oil painting by Hugh Goldwin Riviere, 1906.\n1906 By: Hugh Goldwin RivierePublished: 1906.\n\nCopyrighted work available under Creative Commons Attribution only licence CC BY 4.0 http://creativecommons.org/licenses/by/4.0/', 'L0003723 Henry Solomon Wellcome: three-quarter length. Oil painting', '', 'inherit', 'closed', 'open', '', 'l0003723-henry-solomon-wellcome-three-quarter-length-oil-painting', '', '', '2018-01-20 08:11:52', '2018-01-20 13:11:52', '', 476, 'http://localhost:8888/pmb/wp-content/uploads/2018/01/henry_wellcome.jpg', 0, 'attachment', 'image/jpeg', 0);
