INSERT INTO `pmb_posts` VALUES(545, 1, '2018-01-24 21:44:19', '2018-01-25 02:44:19', '', 'JKay', '"I was hooked from the first page. Each of the 32 chapters offers a completely different type of trailblazer, from actors and mountain climbers to codebreakers and inventors."', 'publish', 'closed', 'closed', '', '545', '', '', '2018-01-24 21:45:46', '2018-01-25 02:45:46', '', 0, 'http://localhost:8888/pmb/?post_type=review&#038;p=545', 2, 'review', '', 0);
INSERT INTO `pmb_posts` VALUES(546, 1, '2018-01-27 13:36:20', '0000-00-00 00:00:00', '', '', 'A tremendous amount of research has obviously gone into this book. Each of the thirty two short stories are written in an entertaining manner that is full of interesting information. When I bought the book I assumed I would read a chapter occasionally as time permitted, but I finished it in two days because I wanted to discover what the next chapter would bring. That next chapter did not disappoint. Highly recommended.', 'draft', 'closed', 'closed', '', '', '', '', '2018-01-27 13:36:20', '2018-01-27 18:36:20', '', 0, 'http://localhost:8888/pmb/?post_type=review&#038;p=546', 1, 'review', '', 0);
